/**
 * 图片加载失败备用方案
 * 解决 GitHub Pages 在国内访问时图片加载失败的问题
 */

// CDN 备用地址
const CDN_BASE = 'https://cdn.jsdelivr.net/gh/zhangyn8/myweb@gh-pages';

// Base64 编码的备用头像（小尺寸版本）
const FALLBACK_AVATAR = 'data:image/jpeg;base64,';

/**
 * 为所有图片添加错误处理
 */
function setupImageFallbacks() {
    // 头像图片特殊处理
    const avatarImg = document.querySelector('.hero-photo');
    if (avatarImg) {
        avatarImg.addEventListener('error', function() {
            console.log('头像加载失败，使用备用方案');
            // 首先尝试 CDN
            const originalSrc = this.src;
            const cdnSrc = CDN_BASE + '/assets/profile-photo.jpg';
            
            if (!this.src.includes('cdn.jsdelivr.net')) {
                this.src = cdnSrc;
            } else {
                // CDN 也失败，使用 Base64
                this.src = FALLBACK_AVATAR;
            }
        });
    }
    
    // 项目图片处理
    const projectImages = document.querySelectorAll('.project-image');
    projectImages.forEach(img => {
        img.addEventListener('error', function() {
            console.log('项目图片加载失败:', this.src);
            const originalSrc = this.src;
            const filename = originalSrc.split('/').pop();
            const cdnSrc = CDN_BASE + '/assets/' + filename;
            
            if (!this.src.includes('cdn.jsdelivr.net')) {
                this.src = cdnSrc;
            } else {
                // CDN 失败，显示占位符
                this.style.display = 'none';
                const placeholder = document.createElement('div');
                placeholder.className = 'image-placeholder';
                placeholder.innerHTML = '🖼️';
                placeholder.style.cssText = `
                    width: 100%;
                    height: 200px;
                    background: #f0f0f0;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    font-size: 3rem;
                    border-radius: 8px;
                `;
                this.parentNode.appendChild(placeholder);
            }
        });
    });
}

/**
 * 主动检查图片加载状态
 */
function checkImageLoading() {
    const images = document.querySelectorAll('img');
    images.forEach(img => {
        if (img.complete) {
            if (img.naturalWidth === 0) {
                // 图片加载失败
                img.dispatchEvent(new Event('error'));
            }
        } else {
            // 监听加载完成
            img.addEventListener('load', function() {
                console.log('图片加载成功:', this.src);
            });
            
            img.addEventListener('error', function() {
                console.log('图片加载失败:', this.src);
            });
        }
    });
}

/**
 * 添加图片加载提示
 */
function addLoadingIndicator() {
    const images = document.querySelectorAll('.hero-photo, .project-image');
    images.forEach(img => {
        // 只在图片还未加载时设置透明度为0
        if (!img.complete) {
            img.style.opacity = '0';
            img.style.transition = 'opacity 0.3s ease';
            
            img.addEventListener('load', function() {
                this.style.opacity = '1';
            });
            
            img.addEventListener('error', function() {
                this.style.opacity = '1';
            });
        }
    });
}

// 页面加载完成后执行
document.addEventListener('DOMContentLoaded', function() {
    setupImageFallbacks();
    addLoadingIndicator();
    
    // 延迟检查，确保所有图片都尝试加载过
    setTimeout(checkImageLoading, 1000);
    setTimeout(checkImageLoading, 3000);
});

// 导出函数供外部使用
window.imageFallback = {
    setupImageFallbacks,
    checkImageLoading,
    addLoadingIndicator
};